<script>
  import gsap from "gsap";
  import { isActive, url } from "@sveltech/routify";
  let isOpen = false;

  let container;
</script>

<style lang="scss">
  .container {
    position: fixed;
    display: flex;
    background-color: rgb(196, 209, 245);
    width: 0;
    top: 0;
    left: 0;
    height: 100%;
    justify-content: space-between;

    button {
      position: absolute;
      left: 100%;
      padding: 0;
      outline: none;
      border: none;
      background-color: transparent;
      line-height: 50%;
      img {
        outline: none;
        height: 1rem;
        background-color: transparent;
      }
    }
  }
</style>

<div bind:this={container} class="container">
  {#if isOpen}
    <div class="content">
      <a href={$url('/')} on:click={() => (isOpen = !isOpen)}>Homepage</a>
    </div>
  {/if}
  <button
    on:click={() => {
      isOpen = !isOpen;
      gsap.to(container, { width: isOpen ? '20%' : '0', duration: 0.6 });
    }}>
    <img
      alt="test"
      src={isOpen ? 'data:image/jpeg;base64,' : 'data:image/png;base64,'} />
  </button>

</div>
