<script>
  import Sidebar from "./../components/Sidebar.svelte";
  import { goto } from "@sveltech/routify";
  import { numberWithCommas } from "./../globals.js";
  import CountryCard from "./../containers/CountryCard.svelte";
  import Card from "./../containers/Card.svelte";
  import { onMount } from "svelte";
  import axios from "axios";

  let worldData;
  let allCountries = [];

  let sortBy = "cases";

  onMount(() => {
    axios.get("https://corona.lmao.ninja/v2/all").then(res => {
      worldData = res.data;
    });
  });

  $: {
    axios
      .get("https://corona.lmao.ninja/v2/countries?sort=" + sortBy)
      .then(res => {
        allCountries = res.data;
      });
  }
</script>

<style lang="scss">
  .cards-container {
    display: flex;
    margin-bottom: 10rem;
    justify-content: space-between;

    @media only screen and (max-width: 800px) {
      flex-wrap: wrap;
    }
  }

  .countries-container {
    display: flex;
    flex-wrap: wrap;
    justify-self: center;
    align-items: center;
  }
</style>

<!-- <Sidebar /> -->

{#if worldData && allCountries}
  <div class="cards-container">
    <Card
      color="#6d6cc5"
      title="Cases worldwide"
      image={'data:image/jpeg;base64,'}
      number={numberWithCommas(worldData.cases)} />
    <Card
      color="red"
      title="Deaths worldwide"
      image={'data:image/png;base64,'}
      number={numberWithCommas(worldData.deaths)} />
    <Card
      color="#429028"
      title="Recovered worldwide"
      image={'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcR7RGpCvhb33oMJ0K_I76tOHyB3PiMYGSV9i8TZqXOE9MmHsAEV&usqp=CAU'}
      number={numberWithCommas(worldData.recovered)} />
  </div>

  <div
    style="display:flex;flex-grow: 1; justify-content: center; align-items:
    center">
    <!-- <h1>Stats per country</h1>

    <select
      value={'-1'}
      on:change={e => {
        $goto('/country/' + e.target.value);
      }}>
      <option value="-1" disabled>Select Country</option>
      {#each allCountries as country}
        <option value={country.countryInfo.iso2}>{country.country}</option>
      {/each}
    </select> -->

    <h3 style="margin-right: 2rem">Sort by</h3>
    <select style="border-radius: 5px; outline: none" bind:value={sortBy}>
      <option value="cases">cases</option>
      <option value="deaths">deaths</option>
      <option value="todayCases">cases today</option>
      <option value="todayDeaths">deaths today</option>
      <option value="recovered">recovered</option>
    </select>
  </div>

  <div class="countries-container">
    {#each allCountries as country, i (i)}
      <CountryCard details={country} />
    {/each}
  </div>
{/if}
